mapper.update:
update wechat_admin_login set 

	adminPassword=#{adminPassword}

where id=#{id}

   		UPDATE wechat_admin_login
		SET adminIdentity = 4,
		adminReason = #{adminReason} where id=#{id}

   		UPDATE wechat_admin_login
		SET adminIdentity = 1,
		adminReason = #{adminReason},adminRole=#{adminRole} where id=#{id}

   		UPDATE wechat_admin_login
		SET adminIdentity = 2,
		adminReason = #{adminReason},adminRole=#{adminRole} where id=#{id}

mapper.select:
select a.id, a.adminNo adminNo, a.adminIdentity adminIdentity, a.creatTime creatTime, c.wechatRoleName adminRole from wechat_admin_login a, wechat_admin_login_mation b, wechat_admin_role c WHERE a.id = b.adminId AND a.adminRole = c.id
and a.adminNo like '%${adminNo}%'
and a.adminIdentity=#{adminIdentity}
and a.adminRole=#{adminRole}
