mapper.select:
SELECT product.id, wechatOption.optionPath, product.productName, product.productPrice, product.productKeyStr, product.productDesc, product.createTime, product.productIntegral, restaurant.productState, restaurant.productNum, restaurant.productNowNum FROM wechat_product_restaurant AS restaurant, wechat_product AS product, wechat_option AS wechatOption, wechat_product_type AS productType WHERE adminRestaurantId = #{id} AND product.id = restaurant.productId AND restaurant.productType = 1 AND wechatOption.id = product.productLogo AND product.productType = productType.id AND productType.id = #{productTypeId}
AND product.productName LIKE "%${productName}%"
ORDER BY product.id ASC

mapper.update:
UPDATE wechat_product_restaurant
SET productNum = #{productNum}
WHERE
	adminRestaurantId = #{id}
AND productType = #{productType}
AND productId in

	#{item}

UPDATE wechat_product_restaurant
SET productNowNum = 0
WHERE
	adminRestaurantId = #{id}
AND productType = #{productType}
AND productId in

	#{item}

UPDATE wechat_product_restaurant
SET
	productState = #{productState}
WHERE
	adminRestaurantId = #{adminRestaurantId}
AND productType = #{productType}
AND productId = #{productId}

UPDATE wechat_product_restaurant
SET productNowNum = productNowNum + 1
WHERE adminRestaurantId = #{id}
AND productType = #{productType}
AND productId = #{productId}
