mapper.update:
UPDATE ${tableName}
SET wetherMake = 1,
	lastUpdateTime = #{nowTime}
WHERE id = #{orderId}
AND orderNumber = #{orderNumber}

UPDATE wechat_product_restaurant
SET productNowNum = productNowNum + #{productNum}
WHERE
	adminRestaurantId = #{id}
AND productId = #{productId}
AND productType = #{productType}

mapper.select:
SELECT orderLog.id, orderLog.orderNumber, orderLog.wetherPaymentTime, orderLog.wetherPayment, orderLog.orderType, orderLog.dayNo, orderLog.phoneNumber, orderLog.orderEatTime FROM ${tableName} AS orderLog WHERE orderLog.wetherPayment = 1 AND orderLog.orderAdminId = #{orderAdminId} AND wetherMake = 0 AND SUBSTRING(orderNumber, 7, 14) = #{shopIdAndDay}
AND orderType = #{orderType}
ORDER BY orderLog.dayNo ASC
