mapper.select:
SELECT orderInf.id, orderInf.orderNumber, orderInf.createTime, orderInf.wetherPaymentTime, orderInf.orderPrice, orderInf.orderType, orderInf.dayNo, orderInf.phoneNumber, orderInf.orderDesc, CASE WHEN orderInf.orderType = 5 THEN ( SELECT CONCAT( address.deliveryAddressProvince, address.deliveryAddressCity, address.deliveryAddressCount, address.deliveryAddressSpecific ) FROM m_wechat_delivery_address AS address WHERE orderInf.orderEatTime = address.id ) ELSE orderInf.orderEatTime END AS orderEatTime, mation.adminId AS shopId, mation.adminShopName FROM ${tableName} AS orderInf, wechat_admin_login_mation AS mation WHERE mation.adminId = orderInf.orderAdminId
AND orderInf.orderAdminId = #{orderAdminId}
AND orderInf.wetherPaymentTime >= #{preTime}
AND #{nowTime} >= orderInf.wetherPaymentTime AND orderInf.orderNumber LIKE "%${nowDay}%" AND wetherPayment=1
